mapper.select:
select 
from comment
where status = 0
and entity_id = #{entityId}
and entity_type = #{entityType}
order by create_time desc
limit #{offset}, #{limit}

SELECT COUNT(id)
FROM comment
WHERE status = 0
and entity_id = #{entityId}
and entity_type = #{entityType}

select 
from comment
where status != 1
and user_id = #{userId}
and entity_type = 1
and exists (
select id from discuss_post where id = comment.entity_id and status != 2
)
order by create_time desc
limit #{offset}, #{limit}
